assignments.value:
={{
    $('When chat message received').isExecuted ? $('When chat message received').first().json.chatInput :
    $('Gmail Trigger').isExecuted ? ($('Gmail Trigger').first().json.text || $('Gmail Trigger').first().json.snippet) :
    $('Webhook').isExecuted ? ($('Webhook').first().json.body?.text || $('Webhook').first().json.text || $('Webhook').first().json.message) :
    ''
  }}

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "vendor": {
      "type": "string",
      "description": "Vendor name from the invoice"
    },
    "invoice_number": {
      "type": "string",
      "description": "Invoice number"
    },
    "po_number": {
      "type": "string",
      "description": "PO number or 'Not Provided'"
    },
    "amount": {
      "type": ["number", "string"],
      "description": "Invoice amount"
    },
    "currency": {
      "type": "string",
      "description": "Currency code (USD, EUR, ILS)"
    },
    "validation_status": {
      "type": "string",
      "description": "Result: approved, missing_po, invalid_po, or insufficient_balance"
    },
    "action_taken": {
      "type": "string",
      "description": "What action was taken"
    },
    "po_remaining": {
      "type": ["number", "string"],
      "description": "Remaining balance on the PO or 0"
    },
    "shortfall": {
      "type": ["number", "string"],
      "description": "Shortfall amount or 0"
    },
    "item_display": {
      "type": "string",
      "description": "Matching item name or 'No match on item name' or 'No PO provided'"
    },
    "email_subject": {
      "type": "string",
      "description": "Subject line for the email reply"
    },
    "email_body": {
      "type": "string",
      "description": "Body of the email reply to vendor"
    },
    "summary": {
      "type": "string",
      "description": "Brief summary of what happened and action taken"
    }
  }
}

parameters.jsonBody:
=  {
    "mock_endpoint": "https://test_id.suitetalk.api.netsuite.com/services/rest/record/v1/vendorBill",
    "request_payload": {
      "entity": {
        "id": "{{ $('Invoice AI Agent').item.json.output.vendor }}"
      },
      "tranDate": "{{ $now.format('yyyy-MM-dd') }}",
      "memo": "Invoice {{ $('Invoice AI Agent').item.json.output.invoice_number }} - Auto-processed by AI Agent",
      "subsidiary": {
        "id": "1"
      },
      "purchaseOrder": {
        "refNumber": "{{ $('Invoice AI Agent').item.json.output.po_number }}"
      },
      "amount":{{ $('Invoice AI Agent').item.json.output.amount }} ,
      "currency": "{{ $('Invoice AI Agent').item.json.output.currency }}",
      "externalId": "AP-{{ $('Invoice AI Agent').item.json.output.invoice_number }}-{{ $now.toMillis() }}"
    },
    "mock_response": {
      "success": true,
      "id": "{{ Math.floor(Math.random() * 100000) }}",
      "refNumber": "VNDBILL-{{ $now.toMillis() }}",
      "status": "pending_approval"
    }
  }

